import axios from "axios";

const axiosCors = axios.create({
    baseURL: process.env.REACT_APP_RESOURCE_URI,
});

// 요청 인터셉터
axiosCors.interceptors.request.use(
    async function (config) {
        const csrf = await axios.get("/oauth2/csrf");
        console.log(csrf);
        config.headers = {
            Accept: "application/json",
            "Content-Type": "application/json",
        };

        config.params = { ...config.params, workCont: window.location.pathname };

        return config;
    },
    function (error) {
        return Promise.reject(error);
    },
);

// 응답 인터셉터
axiosCors.interceptors.response.use(
    function (response) {
        return response;
    },
    async function (error) {
        const originalConfig = error.config;

        if (error.response.status === 401 && !originalConfig._retry) {
            originalConfig._retry = true;

            return axiosCors(originalConfig);
        }

        return Promise.reject(error);
    },
);

export default axiosCors;
