import axios from "axios";

const axiosCors = axios;

// 요청 인터셉터
axiosCors.interceptors.request.use(
    async function (config) {
        const csrf = await axios.get("/oauth2/csrf");
        console.log(csrf);
        config.headers = {
            Accept: "application/json",
            "Content-Type": "application/json",
        };

        console.log(config);

        return config;
    },
    function (error) {
        return Promise.reject(error);
    },
);

